from_id=#{ fromId}, from_name=#{ fromName}, to_id=#{ toId}, to_name=#{ toName}, title=#{ title}, content=#{ content}, read_flag=#{ readFlag}, url=#{ url}, create_date=#{ createDate}, update_date=#{ updateDate},
WHERE id =#{ id}

