mapper.update:
UPDATE t_sys_resource
name=#{ name}, url=#{ url}, pid=#{ pid}, summary=#{ summary}, res_order=#{ resOrder}, create_date=#{ createDate}, update_date=#{ updateDate},
WHERE id =#{ id}

mapper.select:
SELECT

FROM t_sys_resource

    AND id =#{ id}

        SELECT

        FROM t_sys_resource


			order by ${condition.sort}


            limit ${offset}, ${rows}

        SELECT

        FROM t_sys_resource


                AND id =#{ id}


				AND name = #{ name}


				AND url = #{ url}


				AND pid = #{ pid}


				AND summary = #{ summary}


				AND res_order = #{ resOrder}


				AND create_date = #{ createDate}


				AND update_date = #{ updateDate}


        ORDER BY ${orderBy} ${sortBy}

        SELECT

        FROM t_sys_resource


                AND id =#{ id}


				AND name = #{ name}


				AND url = #{ url}


				AND pid = #{ pid}


				AND summary = #{ summary}


				AND res_order = #{ resOrder}


				AND create_date = #{ createDate}


				AND update_date = #{ updateDate}


        limit 0,1
