mapper.update:
UPDATE t_sys_roleresource
role_id=#{ roleId}, role_name=#{ roleName}, resource_id=#{ resourceId}, resource_name=#{ resourceName}, create_date=#{ createDate}, update_date=#{ updateDate},
WHERE id =#{ id}

mapper.select:
SELECT

FROM t_sys_roleresource

    AND id =#{ id}

        SELECT

        FROM t_sys_roleresource


			order by ${condition.sort}


            limit ${offset}, ${rows}

        SELECT

        FROM t_sys_roleresource


                AND id =#{ id}


				AND role_id = #{ roleId}


				AND role_name = #{ roleName}


				AND resource_id = #{ resourceId}


				AND resource_name = #{ resourceName}


				AND create_date = #{ createDate}


				AND update_date = #{ updateDate}


        ORDER BY ${orderBy} ${sortBy}

        SELECT

        FROM t_sys_roleresource


                AND id =#{ id}


				AND role_id = #{ roleId}


				AND role_name = #{ roleName}


				AND resource_id = #{ resourceId}


				AND resource_name = #{ resourceName}


				AND create_date = #{ createDate}


				AND update_date = #{ updateDate}


        limit 0,1
