mapper.update:
UPDATE t_sys_user
user_name=#{ userName}, real_name=#{ realName}, password=#{ password}, mobile=#{ mobile}, email=#{ email}, dep_id=#{ depId}, create_date=#{ createDate}, update_date=#{ updateDate},
WHERE id =#{ id}

mapper.select:
SELECT

FROM t_sys_user

    AND id =#{ id}

        SELECT

