mapper.update:
UPDATE t_sys_version
sys_name=#{ sysName}, sys_version=#{ sysVersion}, build_date=#{ buildDate}, copy_right=#{ copyRight}, create_date=sysdate(),
WHERE id =#{ id}

mapper.delete:
        DELETE FROM  t_sys_version 


